frappe.web_form.on('user_id', (field, value) => {
    console.log("HERE: " + value);
    if (value < 1000) {
        
        frappe.msgprint('Value must be more than 1000');
    }
});

frappe.web_form.on('member_id', (field, value) => {
    console.log("HERE: " + value);
    if (value < 1000) {
        
        frappe.msgprint('Value must be more than 1000');
    }
});

frappe.web_form.after_load = (frm) => {
    $(".ml-2").css("display", "none");
    $(".ml-2").addClass('hidden');
    $(".btn-login-area").css("display", "none");
    $(".navbar-toggler-icon").css("display", "none");
    console.log("Adding the login button")
    var r= $('<input type="button" value="Log In"/>');
    frm.append(r);
}
